(524, 'Bài Không Tên Cuối Cùng', NULL, 'Nhớ em [Am] nhiều nhưng chẳng nói\r\nNói ra [A7] nhiều cũng vậy [Dm] thôi,\r\nÔi đớn [Am] đau đã nhiều [E7] rồi,\r\nMột lời thêm càng buồn [Dm6] thêm [E7] còn hứa [Am] gì\r\n\r\nBiết bao [Am] lần em đã hứa\r\nHứa cho [A7] nhiều rồi lại [Dm] quên,\r\nAnh biết [Am] tin ai bây [E7] giờ,\r\nNgày còn đây người còn [E7] đây.\r\nCuộc sống nào [Am] chờ.\r\n\r\nNày em [F] hỡi con đường em đi [Am] đó,\r\nCon đường em theo [F] đó sẽ đưa em sang [Am] đâu\r\nMưa bên [Dm] chồng có làm em [Am] khóc\r\nCó làm em [C] nhớ những khi mình mặn [E7] nồng\r\n\r\nNày em [F] hỡi  con [Am] đường  em  đi đó,\r\nCon đường em  theo [F] đó đúng hay sao [Am] em [A7]\r\nXa nhau [Dm] rồi  thiên đường thôi [Am] lỡ\r\nCho thần tiên chấp [C] cánh\r\nXót [E7] cho ngườI tình [Am] si.\r\n\r\nSuốt con [Am] đường ai dìu lối,\r\nHãy yêu [A7] nhiều người em [Dm] tôi,\r\nXin gửi [Am] em một lời [E7] chào,\r\nMột lời thương một lời [Dm6] yêu [E7]\r\nLần cuối [Am] cùng', NULL, 211, 35, NULL, 4, '2013-08-05', 'jit', '2013-08-05', 'jit', 'Unpublished', NULL),
(525, 'Bài Thánh Ca Buồn', NULL, 'Bài thánh ca [C] đó còn nhớ không [C] em\r\nNoel[Db7]năm [G#m#] nào chúng mình có [C] nhau\r\nLong [A7]lanh sao [Dm] trời đẹp thêm môi mắt\r\nÁo trắng em [G] bay như cánh thiên [E7] thần\r\nGiọt môi [E7] hôn dưới tháp chuông [C] ngân\r\n\r\nCùng nhau qùy [G7] dưới tượng Chúa cao [C] sang\r\nXin [E7]cho đôi [G#m#] mình suốt đời có [C] nhau\r\nVang [A7]trong đêm [Dm] lạnh bài ca Thiên Chúa\r\nKhẽ hát theo [G] câu đêm thánh vô [E7] cùng\r\nÔi giọng hát [G] em mênh mang [C] buồn\r\n\r\nRồi [G] mùa giá buốt cũng qua [D7] mau\r\nLời hẹn đầu [G] ai nhớ dài [C] lâu\r\nRồi một [F] chiều áo trắng thay [C7] màu\r\nEm qua [Dm] cầu xác pháo bay [G] sau\r\n\r\nLời nguyện mình Chúa có nghe [Dm không\r\nSao bây [G] giờ mình hoài xa [C] vắng\r\nBao nhiêu đêm Chúa xuống dương [F]gian\r\nBấy nhiêu [G] lần anh nhớ người [C] yêu\r\n\r\nRồi những đêm [G7] thánh đường đón [C] Noel\r\nLang thang[Db7] qua [G#m#] miền giáo đường dấu [C] yêu\r\nTiếng[ F#7]thánh ca [Dm] ngày xưa vang đêm tối\r\nNhớ quá đi [G] thôi giọng hát ai [E7] buồn\r\nĐêm thánh vô [G] cùng lạnh giá hồn [C] tôi', NULL, 86, 83, NULL, 20, '2013-08-05', 'jit', '2013-08-05', 'jit', 'Unpublished', NULL),
(526, 'Bạn Tôi', NULL, 'Bạn [Bm] tôi sáng nhịn [D] ăn lên giảng [Bm] đường.\r\nBạn [Bm] tôi sáng [D] đạp xe [A] hai mươi cây [Bm] số.\r\nThằng đi dạy [G] thêm, [A] đứa làm tiếp [D] thị. [Bm]\r\nThằng làm quán [A] cơm, tối [F#m] về một gói mì [Bm] tôm.\r\n\r\nTối [Bm] về kể chuyện nhau [D] nghe.\r\nChuyện buồn [A] vui ngày tháng xa [D] nhà.\r\nTối [Bm] về giọng Bắc Trung [A] Nam.\r\nChia cùng điếu [G] thuốc [F#m] sớt chuyện vui [Bm] buồn.\r\n\r\nĐK:\r\nMiền Tây nước [D] lớn [Bm] đứng ngồi không [E] yên.\r\nMiền Trung lũ [F#m] lụt, suốt đêm không [Bm] ngủ.\r\n\r\nChúng tôi vào đại [D#m] học [A] niềm vui chưa [D] dứt. [C#m]\r\nBao nỗi âu [F#m] lo, dáng [G] mẹ [A] gầy hơn [D] trước.\r\nTóc [E] ba thêm sợi [F#m] bạc.\r\nChiều [A] nay tin bão phương [G] xa.\r\n[E] Lòng con chua [F#m] xót...\r\nCon chưa về, chưa về lòng thắt cơn [Bm] đau.', NULL, 229, 112, NULL, 25, '2013-08-05', 'jit', '2013-08-05', 'jit', 'Unpublished', NULL),
(527, 'Bao Giờ Biết Tương Tư', NULL, 'Ngày [Am] nào cho tôi [F] biết\r\n[Dm] Biết yêu em [Ab7] rồi tôi biết tương [C] tư\r\nNgày [E7] nào biết mong [Am] chờ\r\nBiết rộn [Dm] rã buồn vui đời em dưới [E7] mưa\r\n[F] Ôi biết đem tin [Am] này\r\nVắng [Dm] như lòng [G7] giấy tình yêu lấp [C] đầy\r\n[Dm6] Rồi biết quên câu [F] cười\r\n[E7] Biết cho đôi [Am] dòng lệ rơi\r\n\r\n[A] Tình yêu đã trở [Dbm] lại\r\n[D] Đôi mắt đêm ngày\r\n[A] Vơi hết đọa đày\r\n[Bm] Tà [E7] áo em phơi [A] bầy\r\n[Gb7] Ngón tay em [Bm] dài\r\n[E7] Tiếng yêu không [A] lời\r\n\r\nNgày [Am] nào lòng tôi [F] đã\r\n[Dm] Biết vui biết [G] buồn, ôm mối tương [C] tư\r\nNgày [E7] nào cánh Thiên [Am] Đường\r\nĐã mở [Dm] hé tình yêu là trái táo [E7] thơm...\r\nTôi [F] ghé răng cắn [Am] vào\r\n[Dm] Miệng môi ngọt [G7] đắng, tình yêu cuối [C] đường\r\n[Dm6] Là trối trăng cuối [F] cùng\r\nGiấc [E7] mơ não nùng vội [Am] tan', NULL, 195, 35, NULL, 20, '2013-08-05', 'jit', '2013-08-05', 'jit', 'Unpublished', NULL),
(528, 'Bay Đi Cánh Chim Biển', NULL, '[Am] Bay đi [Dm] cánh chim [Am] biển hiền [E7] lành\r\nChẳng còn giấc [Am] mơ nào để [Dm] giữ đôi chân em\r\n[G] Chẳng còn tiếng nói nào để [C] trách cứ em\r\nKhi [G7] mặt trời đậu trên đôi cánh [E7] vỗ\r\n\r\n[Am] Theo tiếng hát [F] của người thủy [E7] thủ\r\nLượn trên sóng [Am] vỗ về ghềnh [Dm] đá chim bay qua\r\n[G7] Lang thang cánh gió chiều buồn [C] trắng men san hô\r\nĐất [G7] trời rộng sao em không bến [E7] đỗ\r\n\r\n[G7] Giấc mơ của [Am] tôi và [Dm] cánh chim hải [C] âu\r\n[F] Bay ra khỏi tầm tay [E7] và tiếng [Am] sóng\r\n\r\n[Am] Em đã [Dm] muốn ra [Am] đi nhiều [E7] lần\r\nTrời lên tiếng [Am] kêu gọi và [Dm] gió reo tên em\r\n[G] Trùng dương vỗ sóng về để [C] đón bước em\r\nQuay [G7] mặt lại nhìn nhau một lần [E7] cuối\r\n\r\n[Am] Bay đi cánh [F] chim biển hiền [E7] lành\r\nChẳng còn giấc [Am] mơ nào để [Dm] giữ đôi chân em\r\n[G7] Chẳng còn tiếng nói nào để [C] trách cứ em\r\nKhi [G7] mặt trời đậu trên đôi cánh [E7] vỗ\r\n\r\n[G7] Giấc mơ của [Am] tôi và [Dm] cánh chim hải [C] âu\r\n[F] Bay ra khỏi tầm tay [E7] và tiếng [Am] sóng', NULL, 72, 99, NULL, 4, '2013-08-05', 'jit', '2013-08-05', 'jit', 'Unpublished', NULL),
(529, 'Bây Giờ Tháng Mấy', NULL, 'Bây [C] giờ tháng [E7] mấy rồi hỡi [Am] em?\r\nLênh [Em] đênh ngàn mây trôi êm [C] đềm\r\nChiều [Dm] nay nếu em đừng hờn [C] dỗi,\r\ntrách [A7] nhau một lời thôi\r\nTâm [F] hồn mình đâu lẻ [C] loi.\r\n\r\nBây [C] giờ tháng [E7] mấy rồi hỡi [Am] em?\r\nAnh [Em] đi tìm màu hoa em [C] cài\r\nChiều [Dm] nay nhớ em rồi và [C] nhớ\r\náo [A7] em đẹp màu thơ,\r\nmôi [G7] tràn đầy ước [C] mơ\r\n\r\nMai [F] đây anh đưa em đi [Dm] về,\r\nMưa [G7] giăng chiều nắng [C] tàn\r\nCho [F] buốt lạnh chúng [C] mình.\r\nEm [Dm] ơi, thôi [G7] đừng hờn anh [Am] nữa,\r\nnhìn [Dm] nhau buồn vời [G7] vợi,\r\nđể mùa [G7] đông buốt giá bờ vai [C] mềm.\r\n\r\nBây [C] giờ tháng [E7] mấy rồi hỡi [Am] em?\r\nAnh [Em] đi tìm mùa xuân trên [C] đời\r\nMùa [Dm] đông chết đi rồi mùa [C] xuân\r\nmắt [Am] em đẹp trời sao\r\ncho [G7] mình thương nhớ [C] nhau.', NULL, 277, 35, NULL, 23, '2013-08-05', 'jit', '2013-08-05', 'jit', 'Unpublished', NULL),
