(637, 'Đừng Xa Em Đêm Nay', NULL, 'Đừng [Am] xa em đêm nay khi bóng trăng qua hàng cây\r\nĐừng [Dm] xa em đêm nay đêm rất [Am] dài\r\nVòng [Am] tay em cô đơn đêm khuya [Dm] vắng nghe buồn hơn\r\nCòn [F] tim em khát khao yêu [E7] thương.\r\n\r\nĐừng [Am] xa em đêm nay hãy nói anh sẽ ở đây\r\nĐừng [Dm] để em một mình nơi chốn [Am] này\r\nHãy [Am] ôm em trong tay cho em [Dm] biết anh cần em\r\nVà hãy [F] nói [E7] anh vẫn yêu [Am] em.\r\n\r\n[Am] Giọt nước mắt nào\r\n[F] Để trong bóng tối khi nằm [Am] lắng nghe tiếng đêm\r\n[F] Lắng nghe tiếng đêm nghe [E7] nhịp đập con tim\r\nRu em giấc ngủ yên. [Am]\r\n[Am] Đời em vắng lạnh\r\n[F] và anh đã đến như ngọn [Am] nến trong bóng đêm,\r\n[F] Nến trong bóng đêm đang [E7] soi vào tim em\r\nNghe xao xuyến đã ngủ [Am] quên.\r\n\r\nĐừng [Am] xa em đêm nay khu phố quen đã ngủ say\r\nĐừng [Dm] xa em đêm nay đêm rất [Am] dài\r\nHãy [Am] yêu em đêm nay cho quên [Dm] hết đi ngày mai.\r\nĐừng [F] xa em, đừng [E7] xa em đêm [Am] nay', NULL, 72, NULL, NULL, 4, '2013-08-05', 'jit', '2013-08-05', 'jit', 'Unpublished', NULL),
(638, 'Đường Đến Ngày Vinh Quang', NULL, 'Cùng trèo lên [C#] đỉnh núi cao vời vợi\r\nĐể ta khắc [A#m] tên mình trên đời [F#]\r\nDù ta biết [G#] gian nan đang chờ đón [F]\r\nVà trái [A#m] tim vẫn âm thầm [F#]\r\nĐưa bước chân tìm đến những đỉnh vinh quang [G#]\r\n\r\nBầu trời đêm nay [C#] cùng thức với muôn người\r\nMàu cờ khát [A#m] khao ghi chiến công [F#]\r\nTriệu người dân [G#] mong tin những bàn thắng [F]\r\nLời hứa [A#m] ghi trong tim mình [F#]\r\nSẽ khắc tên vinh quang của Việt Nam [G#]\r\n\r\nVà con tim [F#] ta đã ước nguyện cùng nhau [G#] vai kề vai\r\nNiềm vinh quang [F#] ta chia sẻ cùng nhau [G#]\r\n\r\nĐK:\r\nNgày đó [C#] ngày đó sẽ không xa xôi [G#]\r\nVà chúng [F#] ta là người chiến thắng [F]\r\nHồi còi ngân [F#] vang như thúc giục lòng ta thêm tự tin [G#]\r\nVượt gian nan [F#] ta vưon tới những vì sao [G#]\r\n\r\nNgày đó [C#] ngày đó sẽ không xa xôi [G#]\r\nVà chúng [F#] ta là người chiến thắng [F]\r\nĐưòng đến [A#m] những ngày vinh quang [F#] không còn xa\r\nDù khó khăn vẫn còn [C#]\r\n\r\nHoà dòng người cùng [C#] hát vang phố phường\r\nCùng vơi trống [A#m] chiêng thật vang lừng [F#]\r\nGiọt nước mắt [G#] hân hoan đón mừng chiến thắng [F]\r\nNhiều pháo [A#m] hoa thật tưng bừng [F#]\r\nÔi giấc mơ lung linh muôn vì sao [G#]', NULL, 182, NULL, NULL, 25, '2013-08-05', 'jit', '2013-08-05', 'jit', 'Unpublished', NULL),
