(901, 'Lạc Mất Mùa Xuân', NULL, 'Dìu em đến [Am] đem cho đời anh [Dm] thôi hoang vắng\r\n[G] Tim ta say đắm đã yêu em [C] trọn cuộc tình\r\n[Am] Giòng tháng năm đó đã cho tình yêu [Dm] em tha thiết\r\n[E7] Nhưng ái ân để phôi phai\r\n\r\n[Am] Đành với duyên kiếp em bước đi trong [Dm] chiều mưa rơi\r\n[G] Lặng đứng trên bến anh với trông [C] thuyền ra khơi\r\n[Am] Những tháng năm đến lá theo mùa [E7] thu chết\r\nNhưng thu chết\r\n\r\nXuân [Am] về cho cây xanh [Dm] lá\r\nCớ riêng mình anh [G] lạc mất mùa xuân [C]\r\n[Am] Đêm đêm nằm nghe hồn buâng [Dm] khuâng\r\nBên song anh trông [E7] đầu non trăng xế\r\n\r\nThương [Am] bèo trôi theo sông nước [Dm]\r\nBiết bây giờ em [G] lạc bước về đâu [C]\r\n[Am] Tương tư về thương đôi mắt nâu [Dm]\r\nĐêm đêm tìm trong [E] màn tối muôn sao [Am]\r\n\r\n[Đoạn sau tương tự]\r\n\r\nNgồi suốt canh vắng lãng quên sầu thương trong men đắng\r\nCơn say mê đắm sóng dâng cao ngọn thủy triều\r\nRồi theo thấp thoáng bóng em về chìm trong đáy khuất\r\nĐôi mắt u buồn thiên thu\r\n\r\nRồi nắng mai đến mây trắng bay khi tàn cơn say\r\nTình đã xa vắng nỗi nhớ vẫn còn đâu đây\r\nNhững tháng năm đến lá theo mùa thu chết\r\nNhưng thu chết\r\n\r\nXuân về cho cây xanh lá\r\nCớ sao tình ta chỉ có mùa thu\r\nĐêm đêm nằm nghe hồn tương tư\r\nBên song đầu non tàn canh bóng xế\r\n\r\nThương bèo trôi theo muôn hướng\r\nBiết bây giờ em lạc bước về đâu\r\nEm ơi chờ em đến kiếp nao\r\nXin cho ngày sau mình mãi bên nhau', NULL, NULL, 11, NULL, 25, '2013-08-05', 'jit', '2013-08-05', 'jit', 'Unpublished', NULL),
(902, 'Mắt Biếc', NULL, '[C] Nhớ tới năm xưa bên nhau\r\nBước trong chiều mưa [Em] phím ru [F] nhẹ đưa [Dm]\r\nBến cũ đam mê say sưa [G7]\r\nLá thu còn rơi [F] người xa [C] vắng người [G7]\r\nMắt biếc năm xưa nay đâu [C]\r\nCánh sao còn đây [Em] tóc mây [F] nào bay [Dm]\r\nPhố vắng mênh mang mưa rơi [G7]\r\nUớc mơ nào nguôi [F] tình đã phai rồi [C]\r\n\r\n[Cm] Tình yêu như mây khói [G#]\r\nThoảng theo gió buồn [Fm] mơ hồ [Cm]\r\n[G#] Tình yêu như giông tố qua phố đìu hiu [G7]\r\n[Cm] Nhớ dáng xưa yêu kiều [A#]\r\nTrong chiều nhạt nắng [A#7], cung đàn gợi ý [D#]\r\n[Fm] Chờ nhau trong tê tái [G7]\r\n\r\nMắt biếc năm xưa nay đâu [C]\r\nBến ga tịch liêu [Em] vắng xa [F] người yêu [Dm]\r\nLá úa đơn côi bơ vơ [G7]\r\nCuốn theo chiều rơi [F] người xa [C] cách rồi [G7]\r\nDĩ vãng như bao cung tơ [C]\r\nLướt theo chiều mơ [Em] kết muôn [F] bài thơ [Dm]\r\nNuối tiếc yêu đương xa xưa [G7]\r\nTháng năm nào trôi [F] để nhớ nhung buồn [C]\r\n\r\n[Fm] Tình yêu như kiếp [G#] mây trôi [C]', NULL, 148, 35, NULL, 4, '2013-08-05', 'jit', '2013-08-05', 'jit', 'Unpublished', NULL),
