(1109, 'Xin Cám Ơn Chàng Những Đêm Không Ngủ', NULL, 'Xin cám ơn chàng những đêm không ngủ\r\nChàng dễ thương em không thể ngủ\r\nÔi đêm thật êm và nghe được tiếng trái tim đập khẽ nhịp yêu đời\r\nTừ lâu, trái tim em đông lạnh băng giá\r\nNụ cười em nghi hoặc với nhân gian\r\nAnh đã đến làm băng giá rã tan.. ấm tim em tươi thắm môi cười\r\nLàm sao ngờ một ngày em có thể nói\r\nLời nồng nàn từ đáy trái tim\r\nVới người xa cách chằng thể quen\r\nXa như vì sao em không với tới\r\n\r\nXin cám ơn chàng những đêm không ngủ\r\nChàng dễ thương em không thể ngủ\r\n[A]\r\nChàng dễ thương ôi thật dễ thương\r\nXin cám ơn chàng cho em biết được tình yêu\r\nVới niềm hạnh phúc và cay đắng\r\nĐã trờ nên người như hôm nay\r\nĐược biết yêu yêu đời ngất ngây\r\nEm biết lãng mạn say đắm mộng mơ\r\nBiết được tuôn rơi dòng lệ nóng\r\nBiết hạnh phúc thật quanh đâu đây\r\n\r\n[Am]\r\nXin cám ơn chàng những đêm không ngủ\r\nChàng dễ thương em không thể ngủ', NULL, 211, NULL, NULL, 25, '2013-08-05', 'jit', '2013-08-05', 'jit', 'Unpublished', NULL),
(1110, 'Bài Tango Cho Em', NULL, 'Từ ngày có em [B] về,\r\nNhà mình toàn ánh trăng [C#] thề\r\nGiòng nhạc tình đang tắt [F#] lâu,\r\nTuôn trào ngọt ngào như giòng [B] suối.\r\n\r\nAnh yêu phút ban [C#m] đầu, [F#]\r\nĐẹp nghiêng nghiêng dáng em [B] sầu\r\nTrong mắt em buồn về [C#m] mau,\r\nEm ơi có khi [F#] nào lần gặp đây cho mai [B] sau.\r\n\r\nTiếng đàn hòa êm [E] ái,\r\nNhịp bước em thêm lả [B] lơi\r\nCung điệu buồn chơi [C#] vơi, [F#]\r\nĐôi tâm hồn riêng thế [B] giới.\r\n\r\nMình dìu sát đi [E] em\r\nĐể nghe làn hơi [B] cháy\r\nTrong tim nồng [F#] nàn.\r\nTiếc thương chi trời rộng thênh [C#m] thang,\r\nVương vấn để [F#m] rồi một đời cưu [B] mang.\r\n\r\nGiờ mình có nhau [B] rồi,\r\nĐời đẹp vì tiếng em [C#m] cười.\r\nVượt ngàn trùng qua bể [F#] khơi,\r\nDắt dìu cùng về căn nhà [B] mới.\r\n\r\nTa xây vách chung [C#] tình, [F#]\r\nNhiều chông gai có tay [B] mình.\r\nXin cảm ơn đời còn [C#m] nhau,\r\nXin ghi phút ban [F#] đầu bằng bài Tango cho [B] em.', NULL, 242, 94, NULL, 7, '2013-08-05', 'jit', '2013-08-05', 'jit', 'Unpublished', NULL),
(1111, 'Kiếp Nghèo', NULL, '[Am] Đường về đêm nay vắng tanh\r\nRạt rào hạt mưa rớt nhanh\r\nLạnh lùng mưa xuyên áo tơi\r\nMưa chẳng yêu kiếp sống mong manh\r\nLầy lội qua muôn lối quanh\r\nGập ghềnh đường đê tối tăm\r\nNgập ngừng dừng bên mái tranh\r\nNghe trẻ thơ thức giấc bùi ngùi\r\n\r\nÊm êm tiếng hát ngân nga ôi lời mẹ hiền ru thiết tha\r\nKhông gian tím ngắt bao la như thương đường về quá xa\r\nMưa ơi có thấu cho ta lòng lạnh lùng giữa đêm trường\r\nĐời gì chẳng tình thương không yêu thương\r\nThương cho kiếp sống tha hương thân gầy gò gởi cho gió sương\r\nĐôi khi muốn nói yêu ai nhưng ngại ngùng đành lãng phai\r\nĐêm nay giấy trắng tâm tư gởi về người chốn mịt mùng\r\nĐời nghèo lòng nào dám mơ tình chung\r\n\r\nTrời cao có thấu cúi xin người ban phước cho đời con\r\nMột mái tranh yêu, một mối tình chung thủy không hề phai\r\nVà một ngày mai mưa không nghe tiếng khóc trong đêm dài\r\nĐây cả nỗi niềm biết ngày nào ai thấu cho lòng ai', NULL, 242, NULL, NULL, 25, '2013-08-05', 'jit', '2013-08-05', 'jit', 'Unpublished', NULL),
